import React, { useState, useEffect } from 'react';
import './Tutorial.css';

const Tutorial = ({ onComplete }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [visible, setVisible] = useState(true);
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  // Atualizar dimensões da tela quando redimensionada
  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Verificar se o tutorial deve ser mostrado
  useEffect(() => {
    try {
      const showTutorial = localStorage.getItem('showTutorial');
      if (showTutorial === 'false') {
        setVisible(false);
        if (onComplete) onComplete();
      }
    } catch (error) {
      console.error("Erro ao verificar estado do tutorial:", error);
      setVisible(false);
      if (onComplete) onComplete();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Determinar o tipo de dispositivo para mensagens adaptativas
  const isMobile = windowDimensions.width < 768;

  // Configurar os passos do tutorial com mensagens claras
  const tutorialSteps = [
    {
      title: "Bem-vindo à Roleta de Reflexões!",
      description: "Este aplicativo foi criado para fortalecer os laços familiares através de perguntas e reflexões. Vamos aprender como usar.",
      action: isMobile ? "Deslize para a próxima etapa →" : "Próximo →",
    },
    {
      title: "Escolha uma Categoria",
      description: "Na parte superior da tela, selecione entre Família, Crianças ou Valores para diferentes tipos de perguntas.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
    },
    {
      title: "Gire a Roleta",
      description: "Toque no botão 'Girar Roleta' para fazer a roleta girar e receber uma pergunta aleatória da categoria selecionada.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
    },
    {
      title: "Reflita e Compartilhe",
      description: "Após a roleta parar, será exibida uma carta com uma pergunta para reflexão. Discuta com sua família para momentos de conexão significativa.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
    },
    {
      title: "Pronto para Começar!",
      description: "Agora você sabe como usar a Roleta de Reflexões. Divirta-se com sua família!",
      action: "Começar a Jogar",
    },
  ];

  // Navegar para a próxima etapa
  const nextStep = () => {
    if (currentStep < tutorialSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      completeTutorial();
    }
  };

  // Marcar tutorial como concluído
  const completeTutorial = () => {
    try {
      localStorage.setItem('showTutorial', 'false');
    } catch (error) {
      console.error("Erro ao salvar estado do tutorial:", error);
    }
    setVisible(false);
    if (onComplete) onComplete();
  };

  // Se o tutorial não deve ser mostrado
  if (!visible) return null;

  const currentTutorial = tutorialSteps[currentStep];

  return (
    <div className="tutorial-overlay" onClick={nextStep}>
      <div 
        className="tutorial-card center"
        onClick={(e) => e.stopPropagation()}
      >
        <h3>{currentTutorial.title}</h3>
        <p>{currentTutorial.description}</p>
        <button className="tutorial-action" onClick={nextStep}>
          {currentTutorial.action}
        </button>

        <div className="tutorial-progress">
          {tutorialSteps.map((_, index) => (
            <div 
              key={index} 
              className={`tutorial-dot ${index === currentStep ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                setCurrentStep(index);
              }}
            ></div>
          ))}
        </div>

        <button className="tutorial-skip" onClick={completeTutorial}>
          Pular Tutorial
        </button>
      </div>
    </div>
  );
};

export default Tutorial;
