import React, { useState, useEffect } from 'react';
import './Tutorial.css';

const Tutorial = ({ onComplete }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [visible, setVisible] = useState(true);
  const [windowDimensions, setWindowDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  // Atualizar dimensões da tela quando redimensionada
  useEffect(() => {
    const handleResize = () => {
      setWindowDimensions({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Verificar se o tutorial deve ser mostrado
  useEffect(() => {
    try {
      const showTutorial = localStorage.getItem('showTutorial');
      if (showTutorial === 'false') {
        setVisible(false);
        if (onComplete) onComplete();
      }
    } catch (error) {
      console.error("Erro ao verificar estado do tutorial:", error);
      setVisible(false);
      if (onComplete) onComplete();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Determinar o tipo de dispositivo
  const isMobile = windowDimensions.width < 768;
  const isLargeScreen = windowDimensions.width >= 1024;
  const isMacBook = windowDimensions.width >= 1200 && windowDimensions.height <= 900;

  // Configurar os passos do tutorial com posições adaptadas
  const tutorialSteps = [
    {
      title: "Bem-vindo à Roleta de Reflexões!",
      description: "Este aplicativo foi criado para fortalecer os laços familiares através de perguntas e reflexões.",
      action: isMobile ? "Deslize para a próxima etapa →" : "Próximo →",
      position: "center",
    },
    {
      title: "Escolha uma Categoria",
      description: "Selecione entre Família, Crianças ou Valores para diferentes tipos de perguntas.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
      position: "top",
      highlight: "seletor-categoria",
    },
    {
      title: "Gire a Roleta",
      description: "Toque no botão para girar a roleta e receber uma pergunta aleatória.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
      position: "middle",
      highlight: "roleta-container",
    },
    {
      title: "Reflita e Compartilhe",
      description: "Discuta a pergunta selecionada com sua família para momentos de conexão significativa.",
      action: isMobile ? "Deslize para continuar →" : "Próximo →",
      position: "bottom",
      highlight: "carta-reflexao",
    },
    {
      title: "Pronto para Começar!",
      description: "Agora você sabe como usar a Roleta de Reflexões. Divirta-se com sua família!",
      action: "Começar a Jogar",
      position: "center",
    },
  ];

  // Navegar para a próxima etapa
  const nextStep = () => {
    if (currentStep < tutorialSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      completeTutorial();
    }
  };

  // Marcar tutorial como concluído
  const completeTutorial = () => {
    try {
      localStorage.setItem('showTutorial', 'false');
    } catch (error) {
      console.error("Erro ao salvar estado do tutorial:", error);
    }
    setVisible(false);
    if (onComplete) onComplete();
  };

  // Se o tutorial não deve ser mostrado
  if (!visible) return null;

  const currentTutorial = tutorialSteps[currentStep];

  // Ajustar posição do highlight com base nas dimensões da tela
  const getHighlightStyle = (highlightType) => {
    let style = {};

    // Ajustes específicos para MacBook
    if (isMacBook) {
      if (highlightType === 'seletor-categoria') {
        style = { 
          top: '22%',
          height: '60px'
        };
      } 
      else if (highlightType === 'carta-reflexao') {
        style = { 
          bottom: '18%',
          height: '180px'
        };
      }
    }

    return style;
  };

  // Ajustar posição do card explicativo baseado no tipo de tela
  const getCardStyle = (position) => {
    let style = {};

    // Ajustes específicos para MacBook
    if (isMacBook) {
      if (position === 'top') {
        // Card para explicação da categoria (posicionado abaixo do destaque)
        style = {
          bottom: '35%',
          top: 'auto'
        };
      }
      else if (position === 'bottom') {
        // Card para explicação da carta reflexão (posicionado acima do destaque)
        style = {
          top: '65%',
          bottom: 'auto'
        };
      }
    }

    return style;
  };

  return (
    <div className="tutorial-overlay" onClick={nextStep}>
      {currentTutorial.highlight && (
        <div 
          className={`tutorial-highlight ${currentTutorial.highlight}`} 
          style={getHighlightStyle(currentTutorial.highlight)}
        ></div>
      )}

      <div 
        className={`tutorial-card ${currentTutorial.position}`}
        style={getCardStyle(currentTutorial.position)}
        onClick={(e) => e.stopPropagation()}
      >
        <h3>{currentTutorial.title}</h3>
        <p>{currentTutorial.description}</p>
        <button className="tutorial-action" onClick={nextStep}>
          {currentTutorial.action}
        </button>

        <div className="tutorial-progress">
          {tutorialSteps.map((_, index) => (
            <div 
              key={index} 
              className={`tutorial-dot ${index === currentStep ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                setCurrentStep(index);
              }}
            ></div>
          ))}
        </div>

        <button className="tutorial-skip" onClick={completeTutorial}>
          Pular Tutorial
        </button>
      </div>
    </div>
  );
};

export default Tutorial;
