import React, { useContext, useEffect, useState, useCallback } from 'react';
import { setupTabController } from '../../utils/tabController';
import { useNavigate } from 'react-router-dom';
import Roleta from '../../components/Roleta';
import SeletorCategoria from '../../components/SeletorCategoria';
import CartaReflexao from '../../components/CartaReflexao';
import Botao from '../../components/Botao';
import { useRoleta } from '../../hooks/useRoleta';
import { MensagensContext } from '../../context/MensagensContext';
import './Home.css';

const Home = () => {
  const { girar, angulo, girarRoleta } = useRoleta();
  const { cartaSelecionada } = useContext(MensagensContext);
  const navigate = useNavigate();
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [showIntro, setShowIntro] = useState(true);
  const [achievements, setAchievements] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [isShaking, setIsShaking] = useState(false);

  // Detectar agita√ß√£o do dispositivo para girar roleta - definido com useCallback
  const handleDeviceMotion = useCallback((event) => {
    const acceleration = event.accelerationIncludingGravity;

    if (!acceleration) return;

    const movement = 
      Math.abs(acceleration.x) + 
      Math.abs(acceleration.y) + 
      Math.abs(acceleration.z);

    // Se movimento for maior que limite e n√£o estiver girando
    if (movement > 25 && !girar && !isShaking) {
      setIsShaking(true);

      // Vibrar o dispositivo se a API estiver dispon√≠vel
      if ('vibrate' in navigator) {
        navigator.vibrate(200);
      }

      // Girar a roleta
      girarRoleta();

      // Resetar estado ap√≥s um tempo
      setTimeout(() => setIsShaking(false), 1000);
    }
  }, [girar, girarRoleta, isShaking]);

  // Carregar dados do jogador e inicializar estado
  useEffect(() => {
    // Verificar se h√° um jogador atual
    const player = localStorage.getItem('currentPlayer');

    if (player) {
      setCurrentPlayer(JSON.parse(player));
    } else {
      // Redirecionar para a tela inicial se n√£o tiver jogador
      navigate('/');
      return;
    }

    const removerFavorito = (index) => {
      if (!currentPlayer) return;

      // Cria uma nova lista de favoritos sem o item selecionado
      const novosFavoritos = [...favorites];
      novosFavoritos.splice(index, 1);
      setFavorites(novosFavoritos);

      // Atualiza no localStorage
      localStorage.setItem(`favorites_${currentPlayer.id}`, JSON.stringify(novosFavoritos));

      // Feedback t√°til opcional
      if ('vibrate' in navigator) {
        navigator.vibrate(100);
      }
    };

    // Carregar realiza√ß√µes do jogador
    const savedAchievements = localStorage.getItem(`achievements_${JSON.parse(player).id}`);
    if (savedAchievements) {
      setAchievements(JSON.parse(savedAchievements));
    }

    // Carregar favoritos do jogador
    const savedFavorites = localStorage.getItem(`favorites_${JSON.parse(player).id}`);
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }

    // Mostrar anima√ß√£o de introdu√ß√£o apenas na primeira vez
    setTimeout(() => {
      setShowIntro(false);
    }, 2000);

    // Adicionar evento de vibra√ß√£o ao girar
    window.addEventListener("devicemotion", handleDeviceMotion);

    return () => {
      window.removeEventListener("devicemotion", handleDeviceMotion);
    };
  }, [navigate, handleDeviceMotion]);

  const voltarInicio = () => {
    navigate('/');
  };

  // Adicionar uma conquista
  const addAchievement = useCallback((id, title) => {
    if (!achievements.some(a => a.id === id)) {
      const newAchievement = { id, title, date: new Date().toISOString() };
      const updatedAchievements = [...achievements, newAchievement];

      setAchievements(updatedAchievements);
      if (currentPlayer) {
        localStorage.setItem(`achievements_${currentPlayer.id}`, JSON.stringify(updatedAchievements));
      }

      // Mostrar notifica√ß√£o
      showAchievementNotification(title);
    }
  }, [achievements, currentPlayer]);

  // Mostrar notifica√ß√£o de conquista
  const showAchievementNotification = (title) => {
    // Criar elemento de notifica√ß√£o
    const notification = document.createElement('div');
    notification.className = 'achievement-notification';
    notification.innerHTML = `
      <div class="achievement-icon">
        <span role="img" aria-label="Trof√©u">üèÜ</span>
      </div>
      <div class="achievement-content">
        <div class="achievement-title">Conquista Desbloqueada!</div>
        <div class="achievement-desc">${title}</div>
      </div>
    `;

    // Adicionar ao DOM
    document.body.appendChild(notification);

    // Vibrar o dispositivo se a API estiver dispon√≠vel
    if ('vibrate' in navigator) {
      navigator.vibrate([100, 50, 100, 50, 100]);
    }

    // Reproduzir som de conquista se permitido
    const achievementSound = new Audio('/sounds/achievement.mp3');
    achievementSound.play().catch(e => console.log('Som n√£o reproduzido:', e));

    // Animar entrada
    setTimeout(() => {
      notification.classList.add('show');
    }, 10);

    // Remover ap√≥s alguns segundos
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        document.body.removeChild(notification);
      }, 500);
    }, 3000);
  };

  // Adicionar uma mensagem aos favoritos
  const addToFavorites = () => {
    if (!cartaSelecionada || !currentPlayer) return;

    // Verificar se j√° est√° nos favoritos
    if (!favorites.includes(cartaSelecionada)) {
      const updatedFavorites = [...favorites, cartaSelecionada];
      setFavorites(updatedFavorites);

      // Salvar no localStorage
      localStorage.setItem(`favorites_${currentPlayer.id}`, JSON.stringify(updatedFavorites));

      // Vibrar o dispositivo se a API estiver dispon√≠vel
      if ('vibrate' in navigator) {
        navigator.vibrate([50, 50, 50]);
      }

      // Verificar se √© a primeira mensagem favoritada (conquista)
      if (updatedFavorites.length === 1 && !achievements.includes('first_favorite')) {
        addAchievement('first_favorite', 'Primeira Reflex√£o Favorita');
      }
    }
  };

  // Compartilhar mensagem atual
  const shareMessage = () => {
    if (!cartaSelecionada) return;

    if (navigator.share) {
      navigator.share({
        title: 'Roleta de Reflex√µes',
        text: `"${cartaSelecionada}" - Reflita sobre isso com sua fam√≠lia! #RoletaDeReflexoes`,
        url: window.location.href
      })
      .then(() => {
        // Conquista de compartilhamento
        if (!achievements.includes('first_share')) {
          addAchievement('first_share', 'Compartilhou uma Reflex√£o');
        }
      })
      .catch(error => console.log('Erro ao compartilhar:', error));
    } else {
      alert('Seu navegador n√£o suporta compartilhamento direto.');
    }
  };

  // Verificar conquistas quando a roleta parar
  useEffect(() => {
    if (cartaSelecionada && currentPlayer) {
      // Verificar conquista de primeira reflex√£o
      if (!achievements.some(a => a.id === 'first_spin')) {
        addAchievement('first_spin', 'Primeira Reflex√£o');
      }

      // Verificar o n√∫mero de giros (salvo no localStorage)
      const spinCount = parseInt(localStorage.getItem(`spins_${currentPlayer.id}`) || '0') + 1;
      localStorage.setItem(`spins_${currentPlayer.id}`, spinCount.toString());

      // Conquistas baseadas em n√∫mero de giros
      if (spinCount === 10 && !achievements.some(a => a.id === 'ten_spins')) {
        addAchievement('ten_spins', 'Dez Reflex√µes');
      } else if (spinCount === 50 && !achievements.some(a => a.id === 'fifty_spins')) {
        addAchievement('fifty_spins', 'Cinquenta Reflex√µes');
      }
    }
  }, [cartaSelecionada, currentPlayer, achievements, addAchievement]);

  return (
    <div className="home-container">
      {showIntro && currentPlayer && (
        <div className="welcome-overlay">
          <div className="welcome-content">
            <h2>Ol√°, {currentPlayer.name}!</h2>
            <p>Pronto para refletir e compartilhar momentos especiais?</p>
            <div className={`avatar avatar-${currentPlayer.avatar || 1} avatar-large`}></div>
          </div>
        </div>
      )}

      <div className="header">
        <button className="botao-voltar" onClick={voltarInicio}>
          <span className="botao-voltar-icon">‚Üê</span> Voltar
        </button>
        <h1>Roleta de Reflex√µes</h1>

        {currentPlayer && (
          <div className="player-info">
            <div className={`avatar avatar-${currentPlayer.avatar || 1}`}></div>
            <span className="player-name">{currentPlayer.name}</span>
          </div>
        )}
      </div>

      <div className={`jogo-area ${cartaSelecionada ? 'has-result' : ''}`}>
        <div className="controles-area">
          <SeletorCategoria />

          <Roleta girar={girar} angulo={angulo} />

          <Botao 
            texto={girar ? 'Girando...' : (isShaking ? 'Agite o dispositivo' : 'Girar Roleta')} 
            onClick={girarRoleta} 
            disabled={girar || isShaking} 
          />

          {/* Instru√ß√£o para agitar o dispositivo */}
          <p className="shake-instruction">
            Ou agite o dispositivo para girar a roleta!
          </p>
        </div>

        {cartaSelecionada && (
          <div className="resultado-area">
            <CartaReflexao mensagem={cartaSelecionada} />

            <div className="acoes-carta">
              <button 
                className={`acao-botao ${favorites.includes(cartaSelecionada) ? 'ativo' : ''}`} 
                onClick={addToFavorites}
                aria-label="Adicionar aos favoritos"
              >
                <span className="acao-icon">‚ô•</span>
              </button>

              <button 
                className="acao-botao" 
                onClick={shareMessage}
                aria-label="Compartilhar"
              >
                <span className="acao-icon">‚Üó</span>
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Bot√£o para abrir modal de realiza√ß√µes e favoritos */}
      <button 
        className="botao-conquistas"
        onClick={() => {
          document.getElementById('modal-perfil').classList.add('aberto');
          // Configurar o controlador de abas ap√≥s o modal ser aberto
          setTimeout(setupTabController, 100);
        }}
        aria-label="Ver perfil e conquistas"
      >
        <span className="botao-conquistas-icon" role="img" aria-label="Trof√©u">üèÜ</span>
      </button>

      {/* Modal de perfil com conquistas e favoritos */}
      <div id="modal-perfil" className="modal-perfil">
        <div className="modal-conteudo">
          <button 
            className="modal-fechar"
            onClick={() => document.getElementById('modal-perfil').classList.remove('aberto')}
          >
            √ó
          </button>

          <div className="perfil-header">
            {currentPlayer && (
              <>
                <div className={`avatar avatar-${currentPlayer.avatar || 1} avatar-large`}></div>
                <h2>{currentPlayer.name}</h2>
              </>
            )}
          </div>

          <div className="tabs">
            <button className="tab-button active" data-tab="conquistas">Conquistas</button>
            <button className="tab-button" data-tab="favoritos">Favoritos</button>
          </div>

          <div className="tab-content active" id="conquistas">
            {achievements.length === 0 ? (
              <p className="mensagem-vazia">Continue jogando para desbloquear conquistas!</p>
            ) : (
              <div className="lista-conquistas">
                {achievements.map(achievement => (
                  <div className="conquista-item" key={achievement.id}>
                    <div className="conquista-icon">
                      <span role="img" aria-label="Trof√©u">üèÜ</span>
                    </div>
                    <div className="conquista-info">
                      <h3>{achievement.title}</h3>
                      <p>{new Date(achievement.date).toLocaleDateString()}</p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="tab-content" id="favoritos">
            {favorites.length === 0 ? (
              <p className="mensagem-vazia">Marque reflex√µes como favoritas para v√™-las aqui!</p>
            ) : (
              <div className="lista-favoritos">
                {favorites.map((fav, index) => (
                  <div className="favorito-item" key={index}>
                    <p>"{fav}"</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
